import React, { useState } from 'react';
import Header from './../../Common/Header';
import {registerUser} from './../../../api/user';
import { useHistory } from "react-router-dom";
import Author from './../../Common/AuthorComponent';
import './add.css';

export default function ListEmployee (){
    let history = useHistory();
    // const fullname = React.useRef(null);
    // const job = React.useRef(null);
    // const department = React.useRef(null);
    // const email = React.useRef(null);
    // const phone = React.useRef(null);
    // const password = React.useRef(null);
    // const onCreateUser=()=>{
    //     createNewUser(email.current.value,fullname.current.value,job.current.value,department.current.value,,phone.current.value).then(result=>{
    //         toast.success("Thêm Thành Công")
    //         history.push("/employee")
    //     })
    // }

    const [email, setEmail] = useState()
    const [password, setPassword] = useState()
    const [passwordCheck, setPasswordCheck] = useState()
    const [fullName, setFullName] = useState()
    const [job, setJob] = useState()
    const [department, setDepartment] = useState()
    const [phone, setPhone] = useState()

    const onSubmit = async (e) =>{
        e.preventDefault()

        try {
            registerUser(email, password, passwordCheck, fullName, job, department, phone)
        }
        catch (err) {
            err.response.data.msg && setError(err.response.data.msg);
        }
    }

    return (
        <Author>
            <Header></Header>
            <div className="ContainerEdit">
                <div className="EditHeader">
                    <div style={{color:"#000000"}}>Thêm Nhân Viên</div>
                </div>
                <div className="MainEdit">
                    <div>
                        <p className="elementEdit" >Email</p>
                        <div className="elementEdit">
                            <input  type="text" onChange={(e)=>setEmail(e.target.value)}/>
                        </div>
                        <p className="elementEdit" >Password</p>
                        <div className="elementEdit">
                            <input  type="password" onChange={(e)=>setPassword(e.target.value)}/>
                        </div>
                        <div className="elementEdit">
                            <input  type="password" placeholder="verified password" onChange={(e)=>setPasswordCheck(e.target.value)}/>
                        </div>
                        <p className="elementEdit"> Tên</p>
                        <div className="elementEdit">
                            <input  type="text" onChange={(e)=>setFullName(e.target.value)}/>
                        </div>
                        <p className="elementEdit">Chức Vụ</p>
                        <div className="elementEdit">
                            <input  type="text" onChange={(e)=>setJob(e.target.value)}/>
                        </div>
                        <p>Văn Phòng</p>
                        <div className="elementEdit">
                            <input  type="text" onChange={(e)=>setDepartment(e.target.value)}/>
                        </div>
                        <p className="elementEdit" >Phone</p>
                        <div className="elementEdit">
                            <input  type="text" onChange={(e)=>setPhone(e.target.value)}/>
                        </div>
                        <div className="elementEdit">
                            <button className="ButtonData" onClick={onSubmit}>Lưu</button>
                            <button className="ButtonData" onClick={()=>{history.push("/employee")}}>Trở Về</button>
                        </div>
                    </div>
                </div>
            </div>
        </Author>
    )
}
